sys_script_include.script:
var EventUtilsClient = Class.create();
EventUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    fetchEventDetailsByEventId : function(){
		var eventId = this.getParameter('sysparm_eventId');
		var eventGR = new GlideRecord('x_1790195_event_ma_event_record');
		var result = {};
		if(eventGR.get(eventId)){
			result.eventId = eventGR.getDisplayValue('number');
			result.eventName = eventGR.getDisplayValue('x_event_name');
			result.eventDate = eventGR.getDisplayValue('x_event_date');
			result.location = eventGR.getDisplayValue('x_location');
			result.status = eventGR.getDisplayValue('x_status');
		}
		return result;
	},
	checkEventVip : function(){
		var eventId = this.getParameter('sysparm_eventId');
		var eventGR = new GlideRecord('x_1790195_event_ma_event_record');
		if(eventGR.get(eventId)){
			if(eventGR.x_vip)
			return true;
			else
			return false;
		}
	},
    type: 'EventUtilsClient'
});
