sys_script_include.script:
var BudgetUtils = Class.create();
BudgetUtils.prototype = {
    initialize: function() {
    },
	fetchAllBudgets: function() {
        var res = [];
        var gr = new GlideRecord('x_1790195_event_ma_budget');
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                total_budget: gr.getValue('x_total_budget'),
                amount_spent: gr.getValue('x_amount_spent'),
                amount_remaining: gr.getValue('x_amount_remaining'),
                status: gr.getDisplayValue('x_status')
            });
        }
        return JSON.stringify(res);
    },
    totalBudget: function(id) {
        var event = new GlideRecord('x_1790195_event_ma_budget');
        event.addQuery('number', id);
        event.query();
        while (event.next()) {
            event.total_budget = event.getValue('x_total_budget');

            event.update();
        }
    },
	remainingBudget: function(id) {
		var remaining = 0;
        var event = new GlideRecord('x_1790195_event_ma_budget');
        event.addQuery('number', id);
        event.query();
        if(event.next()) {
            remaining = event.getValue('x_amount_remaining');
        }
		return remaining;
    },
	isApproved:function(id){
      var budget = new GlideRecord('x_1790195_event_ma_budget');
        budget.addQuery('number', id);
        budget.query();
        while (budget.next()) {
			if(budget.getValue('x_status') == 'approved')
			{
				return 'Approved';
			}
	}
	return false;
	},
	getEventBudget:function(eventid){
        var res = [];
        var budget = new GlideRecord('x_1790195_event_ma_budget');
        budget.addQuery('x_event_id.number',eventid);
        budget.query();
        if (budget.next()) {
            res.push({
                id: budget.getDisplayValue(),
				totalBudget : budget.getValue('x_total_budget'),
            });}
            return JSON.stringify(res);

    },

    type: 'BudgetUtils'
};
