sys_script_include.script:
var VendorUtils = Class.create();
VendorUtils.prototype = {
    initialize: function() {},

    fetchAllVendors: function() {
        var res = [];
        var gr = new GlideRecord('x_1790195_event_ma_vendor');
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                vendor_name: gr.getValue('x_vendor_name'),
                contact_person: gr.getValue('x_contact_person'),
                contact_number: gr.getValue('x_contact_number'),
                available: gr.getDisplayValue('x_available')
            });
        }
        return JSON.stringify(res);
    },

    getVendorDetails: function(vendorId) {
        var gr = new GlideRecord('x_1790195_event_ma_vendor');
        if (gr.get(vendorId)) {
            return {
                name: gr.getValue('x_vendor_name'),
                contact: gr.getValue('x_contact_number'),
                service_type: gr.getValue('x_email'),
                is_available: gr.getValue('x_available')
            };
        }
        return null;
    },

    isVendorAvailable: function(vendorId) {
        var gr = new GlideRecord('x_1790195_event_ma_vendor');
        if (gr.get(vendorId)) {
            return gr.getValue('x_available') == 'true';
        }
        return false;
    },

    getVendorsByEvent: function(eventId) {
        var list = [];
        var gr = new GlideRecord('x_1790195_event_ma_vendor');
        gr.addQuery('x_event_id.number', eventId);
        gr.query();

        while (gr.next()) {
            list.push({
                name: gr.getValue('x_vendor_name'),
                service_type: gr.getValue('x_contact_person'),
                contact: gr.getValue('x_contact_number'),
                is_available: gr.getValue('x_available')
            });
        }

        return list;
    },

    type: 'VendorUtils'
};
