sys_script_include.script:
var TaskUtils = Class.create();
TaskUtils.prototype = {
    initialize: function(eventid) {
        this.eventid = eventid;
    },

    fetchAllTasks: function() {
        var res = [];
        var gr = new GlideRecord('x_1790195_event_ma_event_task'); // Use your actual task table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                task_name: gr.getValue('x_task_name'),
                state: gr.getDisplayValue('x_status'), // shows display value (e.g., Open/In Progress)
                due_date: gr.getValue('x_due_date')
            });
        }
        return JSON.stringify(res);
    },

    getTasks: function() {
        var res = [];
        var eventtask = new GlideRecord('x_1790195_event_ma_event_task');
        eventtask.addQuery('x_event_id.number', this.eventid);
        eventtask.query();
        if (eventtask.next()) {
            res.push({
                id: eventtask.getDisplayValue()
            });
        }
        return JSON.stringify(res);

    },
    Overdue: function(taskid) {

        var curr = new GlideDateTime();
        var eventtask = new GlideRecord('x_1790195_event_ma_event_task');
        eventtask.addQuery('number', taskid);
        eventtask.query();
        if (eventtask.next()) {
            if (eventtask.getValue('x_due_date') > curr)
                return "OverDue";
        }
        return "Due";
    },
    getTaskById: function(taskid) {
        var res = [];
        var eventtask = new GlideRecord('x_1790195_event_ma_event_task');
        eventtask.addQuery('number', taskid);
        eventtask.query();
        if (eventtask.next()) {
            res.push({
                id: eventtask.getDisplayValue(),
                name: eventtask.getValue('x_task_name')
            });
        }
        return JSON.stringify(res);
    },
    getTaskByDate: function(duedate) {
        var res = [];
        var eventtask = new GlideRecord('x_1790195_event_ma_event_task');
        eventtask.addQuery('x_due_date', duedate);
        eventtask.query();
        if (eventtask.next()) {
            res.push({
                id: eventtask.getDisplayValue(),
                name: eventtask.getValue('x_task_name')
            });
        }
        return JSON.stringify(res);
    },

    type: 'TaskUtils'
};
