sys_script_include.script:
var AttendeeUtils = Class.create();
AttendeeUtils.prototype = {
    initialize: function(eventid) {
        this.eventid = eventid;
    },

    fetchAllAttendees: function() {
        var res = [];
        var gr = new GlideRecord('x_1790195_event_ma_attendee'); // Use your attendee table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                first_name: gr.getValue('x_first_name'),
                last_name: gr.getValue('x_last_name'),
                email: gr.getValue('x_email'),
                status: gr.getDisplayValue('x_status')
            });
        }
        return JSON.stringify(res);
    },

    isRegistered: function() {
        var attendee = new GlideRecord('x_1790195_event_ma_attendee');
        attendee.addQuery('x_event_id.number', this.eventid);
        attendee.query();
        return attendee.hasNext();
    },
    getAttendee: function(id) {
        var res = [];
        var attendee = new GlideRecord('x_1790195_event_ma_attendee');
        attendee.addQuery('number', id);
        attendee.query();
        if (attendee.next()) {
            res.push({
                id: attendee.getDisplayValue(),
                firstname: attendee.getValue('x_first_name'),
                lastname: attendee.getValue('x_last_name')
            });
        }
        return JSON.stringify(res);

    },
    getEventAttendee: function() {
        var res = [];
        var attendee = new GlideRecord('x_1790195_event_ma_attendee');
        attendee.addQuery('x_event_id.number', this.eventid);
        attendee.query();
        if (attendee.next()) {
            res.push({
                id: attendee.getDisplayValue(),
                firstname: attendee.getValue('x_first_name'),
                lastname: attendee.getValue('x_last_name')
            });
        }
        return JSON.stringify(res);

    },

    type: 'AttendeeUtils'
};
