sys_script_include.script:
var VenueUtils = Class.create();
VenueUtils.prototype = {
    initialize: function() {
    },
	fetchAllVenues: function() {
        var res = [];
        var gr = new GlideRecord('x_1790195_event_ma_venue'); 
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                venue_name: gr.getValue('x_venue_name'),
                address: gr.getValue('x_address'),
                capacity: gr.getValue('x_capacity'),
                contact_number: gr.getValue('x_contact_number')
            });
        }
        return JSON.stringify(res);
    },
    type: 'VenueUtils'
};
